                   }                    if ($row['taskdescription'] != '') {                        $html .= '						<tr>						  <td valign="top" valign="top" width="19%">Description</td>						  <td valign="top" width="4%">:</td>						  <td valign="top">' . substr($row['taskdescription'], 0, 125) . '...</td>						</tr>';                    }                    if ($row['firstamnt'] != '') {                        $html .= '						<tr>						  <td valign="top" width="19%">Amount</td>						  <td valign="top" width="4%">:</td>						  <td valign="top">$' . $row['firstamnt'] . '</td>						</tr>';                    }                    if ($row['other'] != '') {                        $html .= '						<tr>						  <td valign="top" valign="top" width="19%">Other</td>						  <td valign="top" valign="top" width="4%">:</td>						  <td valign="top">' . substr($row['other'], 0, 125) . '...</td>						</tr>';                    }                    if ($row['status'] != '') {                        $html .= '						<tr>						  <td valign="top" width="19%">Status</td>						  <td valign="top" width="4%">:</td>						  <td valign="top">' . $status . '</td>						</tr>';                    }                    $html.=$requestrDetail;                    $html.='</table>			</div>';                }                $html .= '<div class="contxtinner1" style="text-align:right">  <a href="mybids">View Details</a></div>';            } else {                $html = '<div class="no-record">No Bid accept yet...</div>';            }            echo $html;            ?>        </div>        <div style="clear:both;">&nbsp;</div>    </div></div><!-- /.row --> <?php// bottom part of the pageinclude_once "include/footer.php";// end here?>